TornLibrary.export = {
    /**
     * Data Export Utilities.
     */
    /**
     * Triggers a browser download for the provided text data.
     * @param {string} dataString - The content of the file.
     * @param {string} filename - The name of the file to be downloaded.
     * @param {string} [mimeType='text/plain'] - The MIME type of the file.
     */
    download(dataString, filename, mimeType = 'text/plain') {
        const blob = new Blob([dataString], { type: `${mimeType};charset=utf-8` });
        const url = URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = filename;
        document.body.appendChild(a);
        a.click();
        document.body.removeChild(a);
        URL.revokeObjectURL(url);
    },

    /**
     * Formats an array of objects as a CSV string.
     * @param {Array<object>} data - The data to format.
     * @param {Array<{key:string, label:string}>} headers - Maps object keys to column labels.
     * @returns {string} The CSV formatted string.
     */
    asCSV(data, headers) {
        const escape = TornLibrary.utils.escapeCSVField;
        const headerRow = headers.map(h => escape(h.label)).join(',');
        const bodyRows = data.map(row => headers.map(h => escape(row[h.key] ?? '')).join(','));
        return [headerRow, ...bodyRows].join('\n');
    },

    /**
     * Formats an array of objects as an HTML table string.
     * @param {Array<object>} data - The data to format.
     * @param {Array<{key:string, label:string}>} headers - Maps object keys to column labels.
     * @returns {string} The HTML table string.
     */
    asHTML(data, headers) {
        const format = {
            table: (h, b) => `<table><thead>${h}</thead><tbody>${b}</tbody></table>`,
            header: (heads) => `<tr>${heads.map(h => `<th>${TornLibrary.utils.escapeHTML(h.label)}</th>`).join('')}</tr>`,
            row: (cells) => `<tr>${cells.map(c => `<td>${TornLibrary.utils.escapeHTML(c)}</td>`).join('')}</tr>`
        };
        const headerRow = format.header(headers);
        const bodyRows = data.map(row => format.row(headers.map(h => row[h.key] ?? ''))).join('');
        return format.table(headerRow, bodyRows);
    },

    /**
     * Formats an array of objects as a Markdown table string.
     * @param {Array<object>} data - The data to format.
     * @param {Array<{key:string, label:string}>} headers - Maps object keys to column labels.
     * @returns {string} The Markdown table string.
     */
    asMarkdown(data, headers) {
        const headerRow = `| ${headers.map(h => h.label).join(' | ')} |`;
        const separatorRow = `| ${headers.map(() => '---').join(' | ')} |`;
        const bodyRows = data.map(row => `| ${headers.map(h => (row[h.key] ?? '').toString().replace(/\|/g, '\\|')).join(' | ')} |`);
        return [headerRow, separatorRow, ...bodyRows].join('\n');
    }
};

TornLibrary.page = {
    /**
     * Page-Specific Helpers & Data Scrapers.
     * Contains sub-modules for interacting with the DOM on specific Torn pages.
     * Example: TornLibrary.page.racing.getDrivers()
     */

    /**
     * Identifies the current page based on the URL. Prioritizes the 'sid' parameter
     * for pages loaded via loader.php, falling back to the filename.
     * @returns {string} The name of the current page (e.g., 'racing', 'itemmarket', 'gym').
     */
    currentPage: () => {
        const params = new URLSearchParams(window.location.search);
        const sid = params.get('sid');
        if (sid) return sid; // Handles pages like loader.php?sid=racing

        const path = window.location.pathname;
        if (path === '/' || path.startsWith('/index.php')) return 'index';

        // Fallback for pages with a direct filename
        const page = path.substring(path.lastIndexOf('/') + 1).split('.')[0];
        return page || 'unknown';
    },

    /**
     * Retrieves the Torn User ID of the user whose profile is being viewed.
     * Returns null if not on a profile page.
     * @returns {string|null} The User ID from the 'XID' URL parameter.
     */
    getProfileId: () => {
        const params = new URLSearchParams(window.location.search);
        return params.get('XID');
    }
};

/**
 * @namespace TornLibrary.page.common
 * @description Functions for interacting with elements common to most Torn pages.
 */
TornLibrary.page.common = {
    _linkAdded: false, // Universal flag to prevent duplicate additions

    /**
     * Adds a new link to the appropriate menu (sidebar for desktop, bottom bar for mobile).
     * It safely waits for the correct menu element to be available before adding the link.
     *
     * @param {object} options - The options for the new link.
     * @param {string} options.text - The label for the link (e.g., 'My Script').
     * @param {string} options.href - The URL the link should point to (e.g., '/messages.php').
     * @param {string} [options.svgIcon] - A string containing an SVG for the icon. A default is provided if omitted.
     */
    addMenuLink: function({ text, href, svgIcon }) {
        if (this._linkAdded) {
            return; // Exit immediately if a link has already been added.
        }

        // --- Helper function to create the desktop sidebar link ---
        const createDesktopLink = () => {
            const areaDiv = document.createElement('div');
            areaDiv.className = 'area-desktop___bpqAS';
            const defaultIcon = `<svg xmlns="http://www.w3.org/2000/svg" stroke="transparent" stroke-width="0" width="16" height="16" viewBox="0 0 16 16"><path d="M8,1a7,7,0,1,0,7,7A7,7,0,0,0,8,1Zm0,12.25A5.25,5.25,0,1,1,13.25,8,5.25,5.25,0,0,1,8,13.25ZM8.5,4.5v4H11V10H7V4.5Z"></path></svg>`;
            areaDiv.innerHTML = `
                <div class="area-row___iBD8N">
                    <a href="${href}" class="desktopLink___SG2RU">
                        <span class="svgIconWrap___AMIqR"><span class="defaultIcon___iiNis mobile___paLva">${svgIcon || defaultIcon}</span></span>
                        <span class="linkName___FoKha">${TornLibrary.utils.escapeHTML(text)}</span>
                    </a>
                </div>`;
            return areaDiv;
        };

        // --- Helper function to create the mobile bottom bar link ---
        const createMobileLink = () => {
            const li = document.createElement('li');
            li.className = 'area-item___j9_9p'; // Class matches other mobile icons
            const defaultIcon = `<svg xmlns="http://www.w3.org/2000/svg" stroke="transparent" stroke-width="0" width="18" height="18" viewBox="-1 0 18 18"><path d="M8,1a7,7,0,1,0,7,7A7,7,0,0,0,8,1ZM8,14.55A1.15,1.15,0,1,1,9.15,13.4,1.14,1.14,0,0,1,8,14.55Z"></path></svg>`;
            li.innerHTML = `
                <a href="${href}" class="mobileLink___xTgRa sidebarMobileLink">
                    <span class="svgIconWrap___AMIqR">
                        <span class="defaultIcon___iiNis mobile___paLva">${svgIcon || defaultIcon}</span>
                    </span>
                    <span>${TornLibrary.utils.escapeHTML(text)}</span>
                </a>`;
            return li;
        };

        // DECISION: Check view and run ONLY the relevant logic.
        if (TornLibrary.utils.isMobile()) {
            // --- MOBILE LOGIC ---
            // The mobile view has a bottom menu bar which is what we'll add to.
            // The selector 'ul.areas-list___CgD_g' targets the list inside that bar.
            TornLibrary.dom.onElementReady('ul.areas-list___CgD_g', (menuList) => {
                if (this._linkAdded) return;
                menuList.appendChild(createMobileLink());
                this._linkAdded = true;
            });
        } else {
            // --- DESKTOP LOGIC ---
            // The desktop view has the permanent sidebar on the left.
            TornLibrary.dom.onElementReady('div.areas-desktop___mqYu6', (areasContainer) => {
                if (this._linkAdded) return;
                areasContainer.appendChild(createDesktopLink());
                this._linkAdded = true;
            });
        }
    }
};

/**
 * @namespace TornLibrary.page.racing
 * @description Helpers for the racing page (torn.com/loader.php?sid=racing).
 */
TornLibrary.page.racing = {
    /**
     * Gets a list of drivers and their details from the current race view.
     * @returns {Array<{name: string, car: string, userId: string}>|null} An array of driver objects, or null if not on a race page or if the leaderboard isn't visible.
     */
    getDrivers: () => {
        // This check is important. It prevents the function from running on the wrong page.
        if (TornLibrary.page.currentPage() !== 'racing') {
            return null;
        }

        const drivers = [];
        // This selector targets each list item in the leaderboard.
        const driverElements = document.querySelectorAll('#leaderBoard > li[id^="lbr-"]');

        if (!driverElements || driverElements.length === 0) {
            console.warn("TornLibrary.page.racing.getDrivers: Could not find driver elements. Are you in a race lobby?");
            return null;
        }

        driverElements.forEach(el => {
            const nameEl = el.querySelector('.name span');
            const carImgEl = el.querySelector('.car img');
            
            // Extracts the User ID from the element's ID (e.g., "lbr-2782979")
            const userIdMatch = el.id.match(/lbr-(\d+)/);

            if (nameEl && carImgEl && userIdMatch) {
                drivers.push({
                    name: nameEl.textContent.trim(),
                    car: carImgEl.getAttribute('title') || 'Unknown Car',
                    userId: userIdMatch[1]
                });
            }
        });

        return drivers.length > 0 ? drivers : null;
    }
};
